import React from 'react'
import { useState } from 'react'


function AddProduct() {
    const[product,setProduct]=useState({
        productName:"",
        description:"",
        price:"",
        status:"",
    });
     const changeHandle=(e)=>{
        const value=e.target.value;
        setProduct({...product,[e.target.value]:value});
     };
     const productResister=(e)=>{
        e.preventDefault;
        console.log(product);
     }
  return (
  <><div className="container mt-2" >
    <div className="row">
        <div className="col-md-6 offset-md-3">
          <div className="card">
            <div className="card-header  fs-3 ">Add Product</div>
            <div className="card-body">
                <form onSubmit={(e)=>productResister(e)}>
                <div className="mb-3 text-start">
                    <label  >Enter Product Name</label>
                    <input type="text" name='productName' className='form-control' onChange={(e)=>changeHandle(e)}/>
                </div>
                <div className="mb-3 text-start">
                    <label  >Enter Description</label>
                    <input type="text" name='description' className='form-control' onChange={(e)=>changeHandle(e)}/>
                </div>
                <div className="mb-3 text-start">
                    <label  >Enter Price</label>
                    <input type="text" name='price' className='form-control' onChange={(e)=>changeHandle(e)}/>
                </div>
                <div className="mb-3 text-start">
                    <label  >Enter Status</label>
                    <input type="text" name='status' className='form-control' onChange={(e)=>changeHandle(e)}/>
                </div>
           <button className='btn btn-primary"  col-md-12'>Submit</button>
                </form>
            </div>
          </div> 
        </div>
    </div>
  </div>
  </>
  )
}

export default AddProduct
